import React, { useState, useEffect } from "react";
import Login from "./Login";

const App = () => {
  // Î™®Îì† HookÏùÑ ÏµúÏÉÅÎã®ÏóêÏÑú Ìò∏Ï∂ú
  const [user, setUser] = useState(null);

  // ÌïúÍµ≠ ÏãúÍ∞Ñ Í∏∞Ï§ÄÏúºÎ°ú Ïò§Îäò ÎÇ†Ïßú Í≥ÑÏÇ∞
  const getTodayDate = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  const [selectedDate, setSelectedDate] = useState(getTodayDate());
  const [mood, setMood] = useState('');
  const [customMood, setCustomMood] = useState('');
  const [diaryEntry, setDiaryEntry] = useState('');
  const [feedback, setFeedback] = useState([]);
  const [diaryEntries, setDiaryEntries] = useState({});
  const [showCustomMoodInput, setShowCustomMoodInput] = useState(false);
  const [learningSummary, setLearningSummary] = useState('');

  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  // Load saved entries from localStorage
  useEffect(() => {
    if (user) { // Î°úÍ∑∏Ïù∏Îêú ÏÉÅÌÉúÏóêÏÑúÎßå Ïã§Ìñâ
      const savedEntries = localStorage.getItem('diaryEntries');
      if (savedEntries) {
        const parsedEntries = JSON.parse(savedEntries);
        setDiaryEntries(parsedEntries);
        // ÌòÑÏû¨ ÏÑ†ÌÉùÎêú ÎÇ†ÏßúÏùò Îç∞Ïù¥ÌÑ∞Îßå Î°úÎìú
        if (parsedEntries[selectedDate]?.learningSummary) {
          setLearningSummary(parsedEntries[selectedDate].learningSummary);
        }
      }
    }
  }, [user]); // selectedDate ÏùòÏ°¥ÏÑ± Ï†úÍ±∞

  // Î°úÍ∑∏Ïù∏ Ìï∏Îì§Îü¨
  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  // Î°úÍ∑∏ÏïÑÏõÉ Ìï∏Îì§Îü¨
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
    localStorage.removeItem('diaryEntries');
  };

  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉúÍ∞Ä ÏïÑÎãàÎ©¥ Î°úÍ∑∏Ïù∏ ÌôîÎ©¥ ÌëúÏãú
  if (!user) {
    return <Login onLogin={handleLogin} />;
  }

  const moods = [
    { name: 'Happy', emoji: 'üòä' },
    { name: 'Sad', emoji: 'üò¢' },
    { name: 'Excited', emoji: 'üéâ' },
    { name: 'Anxious', emoji: 'üò∞' },
    { name: 'Calm', emoji: 'üßò' },
    { name: 'Stressed', emoji: 'üò´' },
    { name: 'Grateful', emoji: 'üôè' },
    { name: 'Tired', emoji: 'üò¥' },
    { name: 'Energetic', emoji: '‚ö°' },
    { name: 'Hopeful', emoji: 'üåü' },
    { name: 'Frustrated', emoji: 'üò§' },
    { name: 'Content', emoji: 'üòå' },
    { name: 'Lonely', emoji: 'üòî' },
    { name: 'Inspired', emoji: 'üí°' },
    { name: 'Bored', emoji: 'ü•±' },
    { name: 'Confident', emoji: 'üí™' },
    { name: 'Nervous', emoji: 'üò¨' },
    { name: 'Peaceful', emoji: 'üïäÔ∏è' },
    { name: 'Overwhelmed', emoji: 'üòµ' },
    { name: 'Curious', emoji: 'üîç' },
    { name: 'Relieved', emoji: 'üòÖ' },
    { name: 'Disappointed', emoji: 'üòû' },
    { name: 'Proud', emoji: 'üèÜ' },
    { name: 'Ashamed', emoji: 'üò≥' },
    { name: 'Optimistic', emoji: '‚òÄÔ∏è' },
    { name: 'Pessimistic', emoji: 'üåßÔ∏è' },
    { name: 'Motivated', emoji: 'üöÄ' },
    { name: 'Drained', emoji: 'üîã' },
    { name: 'Playful', emoji: 'üéà' },
    { name: 'Serious', emoji: 'üëî' }
  ];

  // Save entries to localStorage
  const saveEntries = (entries) => {
    setDiaryEntries(entries);
    try {
