      'ÏûêÏú†Ìòï': 'freestyle',
      'Ï†ëÏòÅ': 'butterfly stroke',
      'Î∞•': 'meal',
      'Ï†êÏã¨': 'lunch',
      'ÏïÑÏπ®': 'breakfast',
      'Ï†ÄÎÖÅ': 'dinner',
      'ÌïôÍµê': 'school',
      'ÌöåÏÇ¨': 'office',
      'ÏπúÍµ¨': 'friend',
      'Í∞ÄÏ°±': 'family',
      'ÏóÑÎßà': 'mom',
      'ÏïÑÎπ†': 'dad',
      'Ìòï': 'older brother',
      'ÎàÑÎÇò': 'older sister',
