      'Ïä¨Ìîî': 'sadness',
      'ÌôîÎÇ®': 'anger',
      'ÎëêÎ†§ÏõÄ': 'fear'
    };

    return dictionary[koreanWord] || null;
  };

  const analyzeSentence = (sentence, index) => {
    const feedbackItems = [];
    let processedSentence = sentence.trim();

    if (!processedSentence) return feedbackItems;

    const originalSentence = processedSentence;
    let hasChanges = false;
    let reasons = []; // Î∞∞Ïó¥Î°ú Î≥ÄÍ≤ΩÌïòÏó¨ Í∞Å Ïò§Î•òÎ≥ÑÎ°ú Íµ¨Î∂Ñ

    // 1. Check for Korean words in English text
    const koreanWords = detectKoreanWords(processedSentence);
    if (koreanWords.length > 0) {
      koreanWords.forEach(koreanWord => {
        const englishEquivalent = suggestEnglishEquivalent(koreanWord);
        if (englishEquivalent) {
          processedSentence = processedSentence.replace(koreanWord, englishEquivalent);
          hasChanges = true;
          reasons.push(`‚Ä¢ '${koreanWord}'ÏùÑ(Î•º) '${englishEquivalent}'Î°ú Î∞îÍæ∏ÏÑ∏Ïöî.\n  ÏòÅÏñ¥ ÏùºÍ∏∞ÏóêÏÑúÎäî ÏòÅÏñ¥ ÌëúÌòÑÏùÑ ÏÇ¨Ïö©ÌïòÎäî Í≤ÉÏù¥ Ï¢ãÏïÑÏöî.`);
        } else {
          reasons.push(`‚Ä¢ '${koreanWord}'ÏùÄ(Îäî) ÌïúÍµ≠Ïñ¥ Îã®Ïñ¥ÏòàÏöî.\n  ÏòÅÏñ¥ ÌëúÌòÑÏùÑ Ï∞æÏïÑÏÑú ÏÇ¨Ïö©Ìï¥Î≥¥ÏÑ∏Ïöî.`);
        }
      });
    }

    // 2. Fix "to do" grammar errors (to did, to went, etc.)
    const toInfinitiveErrors = {
      'to did': 'to do',
      'to went': 'to go',
      'to saw': 'to see',
      'to ate': 'to eat',
      'to drank': 'to drink',
      'to wrote': 'to write',
      'to slept': 'to sleep',
      'to felt': 'to feel',
      'to bought': 'to buy',
      'to brought': 'to bring',
      'to thought': 'to think',
      'to caught': 'to catch',
      'to knew': 'to know',
      'to took': 'to take',
      'to came': 'to come'
    };

    Object.entries(toInfinitiveErrors).forEach(([incorrect, correct]) => {
      const regex = new RegExp(`\\b${incorrect}\\b`, 'gi');
      if (regex.test(processedSentence)) {
        processedSentence = processedSentence.replace(regex, correct);
        hasChanges = true;
        reasons.push(`‚Ä¢ '${incorrect}' ‚Üí '${correct}': 'to' Îí§ÏóêÎäî ÎèôÏÇ¨Ïùò ÏõêÌòïÏùÑ ÏÇ¨Ïö©Ìï¥Ïïº Ìï¥Ïöî.\n  Ïòà: to do, to go, to see, to eat`);
      }
    });

    // 3. Fix common verb tense errors in context
    const contextBasedTenseFixes = {
      'I am go': 'I am going',
      'I am went': 'I went',
      'I am do': 'I am doing',
      'I am did': 'I did',
      'I am eat': 'I am eating',
      'I am ate': 'I ate',
      'I am see': 'I am seeing',
      'I am saw': 'I saw',
      'I am have': 'I have',
      'I am had': 'I had',
