        if (hasTimeIndicator || !processedSentence.match(/\b(now|currently|right now|these days)\b/gi)) {
          processedSentence = processedSentence.replace(regex, past);
          hasChanges = true;
          reasons.push(`‚Ä¢ Í≥ºÍ±∞ ÏùºÏùÑ ÏÑúÏà†Ìï† ÎïåÎäî '${present}' ‚Üí '${past}'Î•º ÏÇ¨Ïö©ÌïòÏÑ∏Ïöî.\n  ÏùºÍ∏∞ÏóêÏÑúÎäî ÏùºÎ∞òÏ†ÅÏúºÎ°ú Í≥ºÍ±∞ÌòïÏùÑ ÏÇ¨Ïö©Ìï¥Ïöî.\n  Ïòà: "I go to school" ‚Üí "I went to school"`);
        }
      }
    });

    // 9. Check for incorrect adjective usage with feelings
    const feelingAdjectives = {
      'frustrating': 'frustrated',
      'exciting': 'excited',
      'boring': 'bored',
      'tiring': 'tired',
      'surprising': 'surprised',
      'interesting': 'interested',
      'confusing': 'confused',
      'amazing': 'amazed',
      'shocking': 'shocked',
      'disappointing': 'disappointed',
