      processedSentence = processedSentence.replace(/\bnot\b/gi, '');
      processedSentence = processedSentence.replace(/\s+/g, ' ').trim();
      hasChanges = true;
      reasons.push(`‚Ä¢ Ïù¥Ï§ë Î∂ÄÏ†ïÏùÑ ÌîºÌïòÏÑ∏Ïöî.\n  'not'Í≥º 'no/never' Îì±ÏùÑ Ìï®Íªò ÏÇ¨Ïö©ÌïòÏßÄ ÎßàÏÑ∏Ïöî.\n  Ïòà: "I don't have no money" ‚Üí "I have no money"`);
