import React, { useState, useEffect } from "react";
import Login from "./Login";

const App = () => {
  // ëª¨ë“  Hookì„ ìµœìƒë‹¨ì—ì„œ í˜¸ì¶œ
  const [user, setUser] = useState(null);

  // í•œêµ­ ì‹œê°„ ê¸°ì¤€ìœ¼ë¡œ ì˜¤ëŠ˜ ë‚ ì§œ ê³„ì‚°
  const getTodayDate = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  const [selectedDate, setSelectedDate] = useState(getTodayDate());
  const [mood, setMood] = useState('');
  const [customMood, setCustomMood] = useState('');
  const [diaryEntry, setDiaryEntry] = useState('');
  const [feedback, setFeedback] = useState([]);
  const [diaryEntries, setDiaryEntries] = useState({});
  const [showCustomMoodInput, setShowCustomMoodInput] = useState(false);
  const [learningSummary, setLearningSummary] = useState('');

  // ë¡œê·¸ì¸ ìƒíƒœ í™•ì¸
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  // Load saved entries from localStorage
  useEffect(() => {
    if (user) { // ë¡œê·¸ì¸ëœ ìƒíƒœì—ì„œë§Œ ì‹¤í–‰
      const savedEntries = localStorage.getItem('diaryEntries');
      if (savedEntries) {
        const parsedEntries = JSON.parse(savedEntries);
        setDiaryEntries(parsedEntries);
        // í˜„ìž¬ ì„ íƒëœ ë‚ ì§œì˜ ë°ì´í„°ë§Œ ë¡œë“œ
        if (parsedEntries[selectedDate]?.learningSummary) {
          setLearningSummary(parsedEntries[selectedDate].learningSummary);
        }
      }
    }
  }, [user]); // selectedDate ì˜ì¡´ì„± ì œê±°

  // ë¡œê·¸ì¸ í•¸ë“¤ëŸ¬
  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  // ë¡œê·¸ì•„ì›ƒ í•¸ë“¤ëŸ¬
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
    localStorage.removeItem('diaryEntries');
  };

  // ë¡œê·¸ì¸ ìƒíƒœê°€ ì•„ë‹ˆë©´ ë¡œê·¸ì¸ í™”ë©´ í‘œì‹œ
  if (!user) {
    return <Login onLogin={handleLogin} />;
  }

  const moods = [
    { name: 'Happy', emoji: 'ðŸ˜Š' },
    { name: 'Sad', emoji: 'ðŸ˜¢' },
    { name: 'Excited', emoji: 'ðŸŽ‰' },
    { name: 'Anxious', emoji: 'ðŸ˜°' },
    { name: 'Calm', emoji: 'ðŸ§˜' },
    { name: 'Stressed', emoji: 'ðŸ˜«' },
    { name: 'Grateful', emoji: 'ðŸ™' },
    { name: 'Tired', emoji: 'ðŸ˜´' },
    { name: 'Energetic', emoji: 'âš¡' },
    { name: 'Hopeful', emoji: 'ðŸŒŸ' },
    { name: 'Frustrated', emoji: 'ðŸ˜¤' },
    { name: 'Content', emoji: 'ðŸ˜Œ' },
    { name: 'Lonely', emoji: 'ðŸ˜”' },
    { name: 'Inspired', emoji: 'ðŸ’¡' },
    { name: 'Bored', emoji: 'ðŸ¥±' },
    { name: 'Confident', emoji: 'ðŸ’ª' },
    { name: 'Nervous', emoji: 'ðŸ˜¬' },
    { name: 'Peaceful', emoji: 'ðŸ•Šï¸' },
    { name: 'Overwhelmed', emoji: 'ðŸ˜µ' },
    { name: 'Curious', emoji: 'ðŸ”' },
    { name: 'Relieved', emoji: 'ðŸ˜…' },
    { name: 'Disappointed', emoji: 'ðŸ˜ž' },
    { name: 'Proud', emoji: 'ðŸ†' },
    { name: 'Ashamed', emoji: 'ðŸ˜³' },
    { name: 'Optimistic', emoji: 'â˜€ï¸' },
    { name: 'Pessimistic', emoji: 'ðŸŒ§ï¸' },
    { name: 'Motivated', emoji: 'ðŸš€' },
    { name: 'Drained', emoji: 'ðŸ”‹' },
    { name: 'Playful', emoji: 'ðŸŽˆ' },
    { name: 'Serious', emoji: 'ðŸ‘”' }
  ];

  // Save entries to localStorage
  const saveEntries = (entries) => {
    setDiaryEntries(entries);
    try {
      localStorage.setItem('diaryEntries', JSON.stringify(entries));
    } catch (e) {
      console.warn('Could not save to localStorage', e);
    }
  };

  const detectKoreanWords = (text) => {
    const koreanRegex = /[\uAC00-\uD7A3]/;
    const words = text.split(/\s+/);
    const koreanWords = [];

    words.forEach(word => {
      const cleanWord = word.replace(/[.,!?;:()"'-]/g, '');
      if (koreanRegex.test(cleanWord) && cleanWord.length > 1) {
        koreanWords.push(cleanWord);
      }
    });

    return koreanWords;
  };

  const suggestEnglishEquivalent = (koreanWord) => {
    const dictionary = {
      'í‰ì˜': 'breaststroke',
      'ë°°ì˜': 'backstroke',
      'ìžìœ í˜•': 'freestyle',
      'ì ‘ì˜': 'butterfly stroke',
      'ë°¥': 'meal',
      'ì ì‹¬': 'lunch',
      'ì•„ì¹¨': 'breakfast',
      'ì €ë…': 'dinner',
      'í•™êµ': 'school',
      'íšŒì‚¬': 'office',
      'ì¹œêµ¬': 'friend',
      'ê°€ì¡±': 'family',
      'ì—„ë§ˆ': 'mom',
      'ì•„ë¹ ': 'dad',
      'í˜•': 'older brother',
      'ëˆ„ë‚˜': 'older sister',
      'ì˜¤ë¹ ': 'older brother (female speaker)',
      'ì–¸ë‹ˆ': 'older sister (female speaker)',
      'ì„ ìƒë‹˜': 'teacher',
      'í•™ìƒ': 'student',
      'ìš´ë™': 'exercise',
      'ê³µë¶€': 'study',
      'ë†€ì´': 'play',
      'ì—¬í–‰': 'trip',
      'íœ´ê°€': 'vacation',
      'ìƒì¼': 'birthday',
      'ê²°í˜¼': 'marriage',
      'ì´ë³„': 'breakup',
      'ì‚¬ëž‘': 'love',
      'ê¸°ì¨': 'joy',
      'ìŠ¬í””': 'sadness',
      'í™”ë‚¨': 'anger',
      'ë‘ë ¤ì›€': 'fear'
    };

    return dictionary[koreanWord] || null;
  };

  const analyzeSentence = (sentence, index) => {
    const feedbackItems = [];
    let processedSentence = sentence.trim();

    if (!processedSentence) return feedbackItems;

    const originalSentence = processedSentence;
    let hasChanges = false;
    let reasonText = "";

    // 1. Check for Korean words in English text
    const koreanWords = detectKoreanWords(processedSentence);
    if (koreanWords.length > 0) {
      koreanWords.forEach(koreanWord => {
        const englishEquivalent = suggestEnglishEquivalent(koreanWord);
        if (englishEquivalent) {
          processedSentence = processedSentence.replace(koreanWord, englishEquivalent);
          hasChanges = true;
          reasonText += `ì¢‹ì€ ì‹œë„ì˜ˆìš”! '${koreanWord}'ì„(ë¥¼) ì˜ì–´ë¡œ í‘œí˜„í•  ë•ŒëŠ” '${englishEquivalent}'ë¼ê³  í•˜ë©´ ë” ìžì—°ìŠ¤ëŸ¬ì›Œìš”. ì˜ì–´ ì¼ê¸°ì—ì„œëŠ” í•œêµ­ì–´ ë‹¨ì–´ë³´ë‹¤ ì˜ì–´ í‘œí˜„ì„ ì‚¬ìš©í•˜ëŠ” ê²ƒì´ ì¢‹ì•„ìš”. `;
