  const generateFeedback = (text) => {
    if (!text.trim()) return [];

    const sentences = text
      .replace(/([.!?])\s*(?=[A-Z])/g, "$1|")
      .replace(/([.!?])\s*(?=\s*[a-z])/g, "$1|")
      .replace(/\n+/g, "|")
      .split("|")
      .map(s => s.trim())
      .filter(s => s.length > 0);

    let allFeedback = [];

    sentences.forEach((sentence, index) => {
      const sentenceFeedback = analyzeSentence(sentence, index);
      allFeedback = allFeedback.concat(sentenceFeedback);
    });

    return allFeedback;
  };

